    def __init__(self):
        self.cache = {}
    def calculate(self, n):
        if n in self.cache:
            return self.cache[n]
        if n in [0, 1]:
            return n
        result = self.calculate(n - 1) + self.calculate(n - 2)
        self.cache[n] = result
        return result

